jQuery(document).ready(function($){
	
	//	On click, saves the aviary app key to the server
	$('#add-aviary-app-key-btn').click(function(){
		
		var data = {
			nonce : aviary_settings.nonce,
			action : 'save_appkey',
			appkey : $('[name=aviary-app-key]').val()
		}
		
		$.post( ajaxurl, data, function(response){
			
			if(response.success){
				$('#aviary-app-key-form').addClass('success');
				$('#aviary-app-key-msg .success').html(response.data.msg);
			}else{
				$('#aviary-app-key-form').addClass('error');
				$('#aviary-app-key-msg .error').html(response.data.err);
			}
			
			
		} );
		
	});

});
